package com.e1i6.notionable.domain.payment.entity;import javax.persistence.Entity;import javax.persistence.Id;import com.e1i6.notionable.domain.payment.dto.PaymentConfirmResDto;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Getter;import lombok.NoArgsConstructor;@Entity@Getter@Builder@NoArgsConstructor@AllArgsConstructorpublic class Payment {	@Id	private String paymentKey;	private Long buyerId;	private Long sellerId;	private Long templateId;	private String title;	private String thumbnail;	private String creatorName;	private Integer price;	private String approvedAt;	public PaymentConfirmResDto toDto() {		return PaymentConfirmResDto.builder()				.templateId(this.templateId)				.title(this.title)				.thumbnail(this.thumbnail)				.creatorName(this.creatorName)				.price(this.price)				.approvedAt(this.approvedAt)				.build();	}}