package com.e1i6.notionable.domain.user.controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RestController;import com.e1i6.notionable.domain.user.data.dto.EmailLoginDto;import com.e1i6.notionable.domain.user.service.UserService;import com.e1i6.notionable.global.auth.JwtProvider;import com.e1i6.notionable.global.common.response.BaseResponse;import com.e1i6.notionable.global.common.response.ResponseCode;import com.e1i6.notionable.global.common.response.ResponseException;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;@RestController@RequiredArgsConstructor@Slf4jpublic class UserController {	private final UserService userService;	private final JwtProvider jwtProvider;	@GetMapping("/login/email")	public BaseResponse<EmailLoginDto> loginWithEmail(String email, String password) {		try {			EmailLoginDto emailLoginDto = userService.userLoginWithEmail(email, password);			return new BaseResponse<>(emailLoginDto);		} catch (ResponseException e) {			return new BaseResponse<>(e.getErrorCode());		} catch (Exception e) {			return new BaseResponse<>(ResponseCode.INTERNAL_SERVER_ERROR);		}	}}