package com.e1i6.notionable.domain.usermailauth.controller;import javax.mail.MessagingException;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;import com.e1i6.notionable.domain.usermailauth.dto.EmailReqDto;import com.e1i6.notionable.domain.usermailauth.service.MailService;import com.e1i6.notionable.global.common.response.BaseResponse;import com.e1i6.notionable.global.common.response.ResponseCode;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;@RestController@RequiredArgsConstructor@Slf4jpublic class UserMailAuthController {	private final MailService mailService;	@PostMapping("/auth/email")	public BaseResponse<String> sendEmailAuthCode(@RequestBody EmailReqDto emailReqDto) {		String email = emailReqDto.getEmail();		log.info("email: {}", email);		try {			String authCode = mailService.sendEmail(email);			return new BaseResponse<>(authCode);		} catch (MessagingException e) {			return new BaseResponse<>(ResponseCode.BAD_REQUEST, e.getMessage());		} catch (Exception e) {			return new BaseResponse<>(ResponseCode.INTERNAL_SERVER_ERROR, e.getMessage());		}	}}