package com.e1i6.notionable.global.auth;import java.security.Key;import java.util.Date;import org.springframework.beans.factory.annotation.Value;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.security.core.Authentication;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.stereotype.Component;import io.jsonwebtoken.Claims;import io.jsonwebtoken.ExpiredJwtException;import io.jsonwebtoken.Jwts;import io.jsonwebtoken.MalformedJwtException;import io.jsonwebtoken.SignatureAlgorithm;import io.jsonwebtoken.UnsupportedJwtException;import io.jsonwebtoken.io.Decoders;import io.jsonwebtoken.security.Keys;import lombok.extern.slf4j.Slf4j;// Spring Security를 사용하여 JWT 기반 인증 구현에 대한 여러 기능 구현// JWT 생성, JWT 검증, 인증 정보 추출@Slf4j@Componentpublic class JwtProvider {	// JWT 서명에 사용되는 비밀 키	private final Key key;	// user에 대한 상세 정보를 조회하는 service	private final UserDetailsService userDetailsService;	// secretKey를 디코딩하여 JWT 서명에 사용할 key를 생성함	// @Value를 통해 application.properties에서 jwt.secret을 가져옴	public JwtProvider(@Value("${jwt.secret}") String secretKey,		UserDetailsService userDetailsService) {		byte[] keyBytes = Decoders.BASE64.decode(secretKey);		this.key = Keys.hmacShaKeyFor(keyBytes);		this.userDetailsService = userDetailsService;	}	// 유저 정보를 가지고 AccessToken, RefreshToken 을 생성하는 메서드	public JwtDto generateToken(Long userId) {		final long expiresInMilliseconds = 60 * 60 * 1000; // 1시간		// 클레임 생성		Claims claims = Jwts.claims().setSubject(String.valueOf(userId));		// 현재 시간		long now = (new Date()).getTime();		// Access Token 생성		Date accessTokenExpiresIn = new Date(now + expiresInMilliseconds);		String accessToken = Jwts.builder()			.setClaims(claims)			.setExpiration(accessTokenExpiresIn)			.signWith(key, SignatureAlgorithm.HS256) // 서명 알고리즘 및 키 설정			.compact(); // 최종적으로 토큰 생성		// Refresh Token 생성		String refreshToken = Jwts.builder()			.setExpiration(new Date(now + expiresInMilliseconds))			.signWith(key, SignatureAlgorithm.HS256)			.compact();		return JwtDto.builder()			.grantType("Bearer")			.accessToken(accessToken)			.refreshToken(refreshToken)			.expirationDate(new Date(now + expiresInMilliseconds))			.build();	}	// JWT 토큰을 복호화하여 토큰에 들어있는 정보를 꺼내는 메서드	public Authentication getAuthentication(String accessToken) {		// 토큰 복호화		// 주어진 Access Token을 파싱하여 클레임(claims)을 추출		Claims claims = parseClaims(accessToken);		// 추출된 클레임에서 사용자의 주체(Subject)를 이용하여 UserDetailsService를 통해 UserDetails를 로드		UserDetails userDetails = userDetailsService.loadUserByUsername(claims.getSubject());		// UserDetails와 권한을 사용하여 Spring Security의 Authentication 객체를 생성		return new UsernamePasswordAuthenticationToken(userDetails, null, userDetails.getAuthorities());	}	// 토큰 정보를 검증하는 메서드	public boolean validateToken(String token) {		try {			// 주어진 토큰을 파싱하고 서명을 검증			Jwts.parserBuilder().setSigningKey(key).build().parseClaimsJws(token);			return true;		} catch (io.jsonwebtoken.security.SecurityException | MalformedJwtException e) {			log.info("Invalid JWT Token", e);		} catch (ExpiredJwtException e) {			log.info("Expired JWT Token", e);		} catch (UnsupportedJwtException e) {			log.info("Unsupported JWT Token", e);		} catch (IllegalArgumentException e) {			log.info("JWT claims string is empty.", e);		}		return false;	}	private Claims parseClaims(String accessToken) {		try {			return Jwts.parserBuilder().setSigningKey(key).build().parseClaimsJws(accessToken).getBody();		} catch (ExpiredJwtException e) {			return e.getClaims();		}	}}