package com.e1i6.notionable.global.filter;import java.io.IOException;import javax.servlet.Filter;import javax.servlet.FilterChain;import javax.servlet.ServletException;import javax.servlet.ServletRequest;import javax.servlet.ServletResponse;import javax.servlet.http.HttpServletRequest;import lombok.extern.slf4j.Slf4j;@Slf4jpublic class RequestUrlLogFilter implements Filter {	@Override	public void doFilter(ServletRequest request, ServletResponse response, FilterChain chain) throws		IOException,		ServletException {		HttpServletRequest httpServletRequest = (HttpServletRequest)request;		log.info("request URL:{}", httpServletRequest.getRequestURL());		chain.doFilter(request, response);	}}